import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  rest = true;
  success = false
  failure = false
  title = 'frontend';
  showPokemonVisible = false

  constructor(private PokemonService: PokemonService) {

  }
  cancelmessage() {
    this.PokemonService.success = false;
    this.PokemonService.failure = false;
    this.PokemonService.rest = true;
  }
  activarShowPokemons() {
    if (this.showPokemonVisible == false) {
      this.showPokemonVisible = true
    } else this.showPokemonVisible = false;
  }

}
